    const addPayData = (newData) => {
        setPayData((prev) => ({ ...prev, ...newData })); // Merge new data into the existing object
    };

    return (
        <PayContext.Provider value={{ payData, setPayData, addUserData }}>
            {children}
        </PayContext.Provider>
    );
}

export const usePayData = () => useContext(PayContext);
