import React from 'react';
import { Box, Typography, Table, TableBody, TableCell, TableRow } from '@mui/material';
import { useCart } from './CartContext';
import { Button } from '@mui/material';

const Cart = () => {
    const { cart, removeFromCart, clearCart } = useCart(); // Access cart data and clearCart function from context

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" gutterBottom>
                Your Cart
            </Typography>
            {cart.length > 0 ? (
                <Table>
                    <TableBody>
                        {cart.map((item, index) => (
                            <TableRow key={index}>
                                <TableCell>{item.group}</TableCell>
                                <TableCell>{item.time}</TableCell>
                                <TableCell>${item.cost}</TableCell>
                                <TableCell>
                                    <Button
                                        variant="outlined"
                                        color="error"
                                        onClick={() => removeFromCart(item)}
                                    >
                                        Remove
                                    </Button>
                                </TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            ) : (
                <Typography>No items in cart.</Typography>
            )}
            <Button variant="contained" onClick={clearCart} sx={{ mt: 2 }}>
                Clear Cart
            </Button>
        </Box>
    );
};

export default Cart;
