import React, { useState } from "react";
import { Button, Grid, Typography, Radio, RadioGroup, FormControlLabel, FormControl, FormLabel, Paper } from "@mui/material";

// Schedule Data
const schedule = {
    Monday: [
        { group: "Adults", time: "12PM-2PM", cost: 15 },
        { group: "Ages 6-8", time: "2PM-8PM", cost: 15 },
        { group: "Ages 8-12", time: "3:30PM-8PM", cost: 15 },
    ],
    Tuesday: [
        { group: "Adults", time: "12PM-2PM", cost: 15 },
        { group: "Ages 6-8", time: "2PM-8PM", cost: 15 },
        { group: "Ages 8-12", time: "3:30PM-8PM", cost: 15 },
    ],
    Wednesday: [
        { group: "Adults", time: "12PM-2PM", cost: 15 },
        { group: "Ages 6-8", time: "2PM-8PM", cost: 15 },
        { group: "Ages 8-12", time: "3:30PM-8PM", cost: 15 },
    ],
    Thursday: [
        { group: "Adults", time: "12PM-2PM", cost: 15 },
        { group: "Ages 6-8", time: "2PM-8PM", cost: 15 },
        { group: "Ages 8-12", time: "3:30PM-8PM", cost: 15 },
    ],
    Saturday: [
        { group: "Ages 4-6", time: "10AM-2:30PM", cost: 15 },
        { group: "Ages 6-8", time: "11:30AM-2:30PM", cost: 15 },
        { group: "Ages 8-12", time: "1PM-2:30PM", cost: 15 },
        { group: "Adults", time: "2:30PM-4PM", cost: 15 },
    ],
};

function CalendarApp() {
    const [selectedDate, setSelectedDate] = useState(null);
    const [cart, setCart] = useState([]);

    // Get current date and display current month
    const date = new Date();
    const month = date.toLocaleString("en-US", { month: "long" });
    const year = date.getFullYear();

    // Generate the days of the month
    const firstDayOfMonth = new Date(year, date.getMonth(), 1);
    const lastDayOfMonth = new Date(year, date.getMonth() + 1, 0);
    const numberOfDays = lastDayOfMonth.getDate();
    const firstDayIndex = firstDayOfMonth.getDay();

    const handleDayClick = (day) => {
        setSelectedDate(day);
    };

    const handleCartAdd = (timeSlot, cost) => {
        setCart([...cart, { timeSlot, cost }]);
    };

    const handleDeleteCartItem = (index) => {
        const updatedCart = cart.filter((_, i) => i !== index);
        setCart(updatedCart);
    };

    const handleMonthChange = (direction) => {
        const newDate = new Date(year, date.getMonth() + direction, 1);
        const newMonth = newDate.toLocaleString("en-US", { month: "long" });
        // Here you can add logic to update the state with the new month
        console.log("New Month:", newMonth);
    };

    return (
        <div style={{ padding: 20 }}>
            <Typography variant="h4" align="center">
                {month} {year}
            </Typography>
            <Grid container spacing={2} justifyContent="center" style={{ marginTop: 20 }}>
                <Grid item>
                    <Button variant="contained" onClick={() => handleMonthChange(-1)}>
                        ◀
                    </Button>
                </Grid>
                <Grid item>
                    <Button variant="contained" onClick={() => handleMonthChange(1)}>
                        ▶
                    </Button>
                </Grid>
            </Grid>

            <Grid container spacing={1} justifyContent="center" style={{ marginTop: 20 }}>
                {["Su", "Mo", "Tu", "We", "Th", "Fr", "Sa"].map((day) => (
                    <Grid item key={day} xs={1}>
                        <Typography variant="h6" align="center">{day}</Typography>
                    </Grid>
                ))}

                {Array.from({ length: firstDayIndex }).map((_, index) => (
                    <Grid item key={`empty-${index}`} xs={1}></Grid>
                ))}

                {Array.from({ length: numberOfDays }).map((_, index) => {
                    const day = index + 1;
                    return (
                        <Grid item key={day} xs={1}>
                            <Paper
                                style={{
                                    padding: 10,
                                    textAlign: "center",
                                    cursor: "pointer",
                                    backgroundColor: selectedDate === day ? "#cce7ff" : "transparent",
                                }}
                                onClick={() => handleDayClick(day)}
                            >
                                {day}
                            </Paper>
                        </Grid>
                    );
                })}
            </Grid>

            {selectedDate && (
                <div style={{ marginTop: 20 }}>
                    <Typography variant="h6">Selected Date: {selectedDate}</Typography>
                    <div style={{ marginTop: 10 }}>
                        <FormControl component="fieldset">
                            <FormLabel component="legend">Choose a Class</FormLabel>
                            <RadioGroup>
                                {schedule[Object.keys(schedule)[date.getDay()]].map((slot) => (
                                    <FormControlLabel
                                        key={slot.group}
                                        control={<Radio />}
                                        label={`${slot.group} - ${slot.time}`}
                                        onClick={() => handleCartAdd(`${slot.group} - ${slot.time}`, slot.cost)}
                                    />
                                ))}
                            </RadioGroup>
                        </FormControl>
                    </div>
                </div>
            )}

            <div style={{ marginTop: 20 }}>
                <Typography variant="h5">Cart</Typography>
                {cart.length === 0 ? (
                    <Typography>No items in cart</Typography>
                ) : (
                    cart.map((item, index) => (
                        <Paper key={index} style={{ marginBottom: 10, padding: 10 }}>
                            <Typography>{item.timeSlot} - ${item.cost}</Typography>
                            <Button variant="outlined" onClick={() => handleDeleteCartItem(index)}>
                                Remove
                            </Button>
                        </Paper>
                    ))
                )}
                <Typography variant="h6" style={{ marginTop: 10 }}>
                    Total: ${cart.reduce((total, item) => total + item.cost, 0)}
                </Typography>
            </div>
        </div>
    );
}

export default CalendarApp;
