import React, { useState } from "react";
import {
    Box,
    Grid,
    Typography,
    Button,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableRow,
} from "@mui/material";

const Calendar = () => {
    const [selectedDate, setSelectedDate] = useState("");
    const schedule = {
        Monday: [
            { group: "Adults", time: "12PM-2PM", cost: 15 },
            { group: "Ages 6-8", time: "2PM-8PM", cost: 15 },
        ],
        // Add the rest of the schedule here
    };

    const handleDayClick = (day) => {
        setSelectedDate(day);
    };

    return (
        <Box sx={{ p: 3 }}>
            <Typography variant="h4" gutterBottom>
                Class Calendar
            </Typography>
            <Grid container spacing={2}>
                <Grid item xs={12} md={8}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h6">Calendar</Typography>
                        <Button onClick={() => handleDayClick("Monday")}>Monday</Button>
                        {/* Add more buttons for days */}
                    </Paper>
                </Grid>
                <Grid item xs={12} md={4}>
                    <Paper sx={{ p: 2 }}>
                        <Typography variant="h6">Selected Date: {selectedDate}</Typography>
                        {schedule[selectedDate] && (
                            <Table>
                                <TableBody>
                                    {schedule[selectedDate].map((slot, index) => (
                                        <TableRow key={index}>
                                            <TableCell>{slot.group}</TableCell>
                                            <TableCell>{slot.time}</TableCell>
                                            <TableCell>${slot.cost}</TableCell>
                                        </TableRow>
                                    ))}
                                </TableBody>
                            </Table>
                        )}
                    </Paper>
                </Grid>
            </Grid>
        </Box>
    );
};

export default Calendar;
