// CartContext.js
import React, { createContext, useState, useContext } from 'react';

// 1. Create a Cart Context
const CartContext = createContext();

// 2. Create a CartProvider component to provide the cart context to children
export const CartProvider = ({ children }) => {
    const [cart, setCart] = useState([]);

    // Function to add an item to the cart
    const addToCart = (item) => {
        setCart((prevCart) => [...prevCart, item]);
    };

    // Function to remove an item from the cart
    const removeFromCart = (itemIndex) => {
        setCart((prevCart) => prevCart.filter((_, index) => index !== itemIndex));
    };

    // Function to clear the cart
    const clearCart = () => {
        setCart([]);
    };

    // 3. Return the provider that makes the cart context accessible to the rest of the app
    return (
        <CartContext.Provider value={{ cart, addToCart, removeFromCart, clearCart }}>
            {children}
        </CartContext.Provider>
    );
};

// 4. Custom hook to use the Cart context
export const useCart = () => useContext(CartContext);
